mapper.select:
select
`id`,`name`,`value`,`type`,`description`,`sort`,`parent_id`,`create_by`,`create_date`,`update_by`,`update_date`,`remarks`,`del_flag`
,(SELECT count(bb.id) from sys_dict as bb where bb.parent_id=aa.id) as `childNum`
from sys_dict as aa

	 and id = #{id} 
	 and name like '%${name}%' 
	 and value = #{value} 
	 and type = #{type} 
	 and description = #{description} 
	 and sort = #{sort} 
	 and parent_id = #{parentId} 
	 and create_by = #{createBy} 
	 and create_date = #{createDate} 
	 and update_by = #{updateBy} 
	 and update_date = #{updateDate} 
	 and remarks = #{remarks} 
	 and del_flag = #{delFlag} 
	and parent_id='0'



		order by ${sort} ${order}


		order by id



	limit #{offset}, #{limit}

select count(*) from sys_dict

	 and id = #{id} 
	 and name = #{name} 
	 and value = #{value} 
	 and type = #{type} 
	 and description = #{description} 
	 and sort = #{sort} 
	 and parent_id = #{parentId} 
	 and create_by = #{createBy} 
	 and create_date = #{createDate} 
	 and update_by = #{updateBy} 
	 and update_date = #{updateDate} 
	 and remarks = #{remarks} 
	 and del_flag = #{delFlag} 
	and parent_id='0'

select `id`,`name`,`value`,`type`,`description`,`sort`,`parent_id`,`create_by`,`create_date`,`update_by`,`update_date`,`remarks`,`del_flag` from sys_dict
and parent_id = #{value}
order by id

select `id`,`name`,`value`,`type`,`description`,`sort`,`parent_id`,`create_by`,`create_date`,`update_by`,`update_date`,`remarks`,`del_flag` ,(SELECT count(bb.id) from sys_dict as bb where bb.parent_id=aa.id) as `childNum` from sys_dict as aa
and parent_id=#{parentId}
order by id desc

select
`id`,`name`,`value`,`type`,`description`,`sort`,`parent_id`,`create_by`,`create_date`,`update_by`,`update_date`,`remarks`,`del_flag`
from sys_dict as aa

	and value = #{value}
	and type = #{type}
