mapper.select:
		SELECT j.id,j.`name`,d.name type,j.remarks,j.cperson,j.cdate,j.`status` from jjyy j LEFT JOIN sys_dict d ON d.`value` = j.type

		  		   and j.id = #{id} 
		  		   and j.name like '%${name}%'  
		  		   and j.type = #{type} 
		  		   and j.remarks = #{remarks} 
		  		   and j.cdate = #{cdate} 
		  		   and j.cperson = #{cperson} 
		  		   and j.status = #{status} 
			       AND d.type = 'JJYYLX' and j.status = '0'



                order by ${sort} ${order}


                order by j.cdate desc



			limit #{offset}, #{limit}

SELECT count(*) from jjyy j LEFT JOIN sys_dict d ON d.`value` = j.type

  		   and j.id = #{id} 
  		   and j.name like '%${name}%'  
  		   and j.type = #{type} 
  		   and j.remarks = #{remarks} 
  		   and j.cdate = #{cdate} 
  		   and j.cperson = #{cperson} 
  		   and j.status = #{status} 
	 AND d.type = 'JJYYLX' and j.status = '0'
