mapper.select:
SELECT
t.`LQBZDW_TYWYSBM`,
t.`DWMC`,
t.`DZMC`,
t.`CZHM`,
t.`DW_JYQK`,
t.`LQBZLBDM`,
t.`BZNL_JYQK`,
t.`BZGS_JYQK`,
t.`LXR_XM`,
t.`LXR_LXDH`,
t.`DQJD`,
t.`DQWD`,
t.`XFJYJG_TYWYSBM`,
t.`XZQHDM`,
t.`cdate`,
t.`cperson`,
t.`status`,
d.`name` area,
d1.`name` city,
d2.`name` province,
dept.`dwmc` XFJYJG,
d3.`name` LQBZLB
FROM
jczy_lqbzdw t
left join sys_dict d on t.XZQHDM=d.`value`
left join sys_dict d1 on d1.id = d.parent_id
left join sys_dict d2 on d2.id=d1.parent_id
left join sys_dept dept on t.XFJYJG_TYWYSBM = dept.XFJYJG_TYWYSBM
left join sys_dict d3 on d3.`value`=t.LQBZLBDM and d3.parent_id=(SELECT `id` from `sys_dict` where type='LQBZLBDM' and parent_id='0')
where t.status=0
    and t.LQBZDW_TYWYSBM = #{lqbzdwTywysbm}
    and t.DWMC like '%${dwmc}%'
    and t.DZMC = #{dzmc}
    and t.CZHM = #{czhm}
    and t.DW_JYQK = #{dwJyqk}
    and t.LQBZLBDM = #{lqbzlbdm}
    and t.BZNL_JYQK = #{bznlJyqk}
    and t.BZGS_JYQK = #{bzgsJyqk}
    and t.LXR_XM = #{lxrXm}
    and t.LXR_LXDH = #{lxrLxdh}
    and t.DQJD = #{dqjd}
    and t.DQWD = #{dqwd}
    and t.XFJYJG_TYWYSBM = #{xfjyjgTywysbm}
    and t.XFJYJG_TYWYSBM in

            #{item.xfjyjgTywysbm}


    and t.XZQHDM = #{xzqhdm}
    and t.cdate = #{cdate}
    and t.cperson = #{cperson}
    and t.status = #{status}


        order by ${sort} ${order}


        order by t.cdate desc



    limit #{offset}, #{limit}

select count(*) from jczy_lqbzdw where status=0
    and LQBZDW_TYWYSBM = #{lqbzdwTywysbm}
    and DWMC like '%${dwmc}%'
    and DZMC = #{dzmc}
    and CZHM = #{czhm}
    and DW_JYQK = #{dwJyqk}
    and LQBZLBDM = #{lqbzlbdm}
    and BZNL_JYQK = #{bznlJyqk}
    and BZGS_JYQK = #{bzgsJyqk}
    and LXR_XM = #{lxrXm}
    and LXR_LXDH = #{lxrLxdh}
    and DQJD = #{dqjd}
    and DQWD = #{dqwd}
    and XFJYJG_TYWYSBM = #{xfjyjgTywysbm}
    and XFJYJG_TYWYSBM in

            #{item.xfjyjgTywysbm}


    and XZQHDM = #{xzqhdm}
    and cdate = #{cdate}
    and cperson = #{cperson}
    and status = #{status}
