			       and t.deptcode = #{deptCode} 
			and t.XFJYJG_TYWYSBM in

					#{item.xfjyjgTywysbm}





                order by ${sort} ${order}


                order by t.create_time desc



			limit #{offset}, #{limit}

select t.`id`,t.`video_channel`,t.`name`,t.`channel_type`,t.`XFJYJG_TYWYSBM`,t.`create_time`,t.deptcode from 4g_video_channel t where 1=1 and t.name like '%${name}%' and t.XFJYJG_TYWYSBM IS NULL
order by ${sort} ${order}
order by t.create_time desc
limit #{offset}, #{limit}

