mapper.select:
		select sx.`id`,cllx.`name` as `cllx`,`clsx`,sfzs.`name` as `sfzs`,sfcyjs.`name` as `sfcyjs`,`dw`,sx.`cdate`,sx.`cperson`,sx.`status`
		from jczy_xfcl_sx sx
		left join sys_dict cllx
		on sx.cllx = cllx.`value` and cllx.type='XFZBLXDM'
		LEFT JOIN  sys_dict sfzs
		on sx.sfzs = sfzs.`value` and sfzs.type='s_f'
		LEFT JOIN  sys_dict sfcyjs
		on sx.sfcyjs = sfcyjs.`value` and sfcyjs.type='s_f'

		   and sx.id = #{id} 
		   and sx.cllx = #{cllx} 
		   and sx.clsx like '%${clsx}%' 
		   and sx.sfzs = #{sfzs} 
		   and sx.sfcyjs = #{sfcyjs} 
		   and sx.dw = #{dw} 
		   and sx.cdate = #{cdate} 
		   and sx.cperson = #{cperson} 
		   and sx.status = #{status} 



                order by ${sort} ${order}


                order by sx.id desc



			limit #{offset}, #{limit}

select count(*) from jczy_xfcl_sx

  		   and id = #{id} 
  		   and cllx = #{cllx} 
  		   and clsx = #{clsx} 
  		   and sfzs = #{sfzs} 
  		   and sfcyjs = #{sfcyjs} 
  		   and dw = #{dw} 
  		   and cdate = #{cdate} 
  		   and cperson = #{cperson} 
  		   and status = #{status}
