mapper.select:
SELECT
t.`XFSH_TYWYSBM`,
t.`MC`,
t.`DZMC`,
t.`GD`,
t.`DQJD`,
t.`DQWD`,
t.`SYKYZTLBDM`,
t.`SSLD_MC`,
t.`SSGW_MC`,
t.`XFJSGWXSLXDM`,
t.`GWZJ_KD`,
t.`GW_YL`,
t.`LL`,
t.`JSGZJ_KD`,
t.`CSGZJ_KD`,
t.`JSCDS_SL`,
t.`GS_DWMC`,
t.`GL_DWMC`,
t.`WB_DWMC`,
t.`LXR_XM`,
t.`LXR_LXDH`,
t.`XFJYJG_TYWYSBM`,
t.`XZQHDM`,
t.`JIZ_RQ`,
t.`QY_RQ`,
t.`cdate`,
t.`cperson`,
t.`status` ,
dept.`dwmc` XFJYJG,
d.`name` SYKYZTLB,
d1.`name` XFJSGWXSLX,
d5.`name` area,
d6.`name` city,
d7.`name` province
FROM
jczy_xfsh t
LEFT JOIN sys_dept dept ON t.XFJYJG_TYWYSBM = dept.XFJYJG_TYWYSBM
left join sys_dict d on t.SYKYZTLBDM=d.`value` and d.description='水源可用状态类别代码'
left join sys_dict d1 on t.XFJSGWXSLXDM=d1.`value` and d1.description='消防给水管网形式类型代码'
LEFT JOIN sys_dict d5 ON t.XZQHDM = d5.`value`
LEFT JOIN sys_dict d6 ON d6.id = d5.parent_id
LEFT JOIN sys_dict d7 ON d7.id = d6.parent_id

    and XFSH_TYWYSBM = #{xfshTywysbm}
    and MC like '%${mc}%'
    and DZMC like '%${dzmc}%'
    and GD = #{gd}
    and DQJD = #{dqjd}
    and DQWD = #{dqwd}
    and SYKYZTLBDM = #{sykyztlbdm}
    and SSLD_MC = #{ssldMc}
    and SSGW_MC = #{ssgwMc}
    and XFJSGWXSLXDM = #{xfjsgwxslxdm}
    and GWZJ_KD = #{gwzjKd}
    and GW_YL = #{gwYl}
    and LL = #{ll}
    and JSGZJ_KD = #{jsgzjKd}
    and CSGZJ_KD = #{csgzjKd}
    and JSCDS_SL = #{jscdsSl}
    and GS_DWMC = #{gsDwmc}
    and GL_DWMC = #{glDwmc}
    and WB_DWMC = #{wbDwmc}
    and LXR_XM = #{lxrXm}
    and LXR_LXDH = #{lxrLxdh}
    and t.XFJYJG_TYWYSBM = #{xfjyjgTywysbm}
    and t.XFJYJG_TYWYSBM in

            #{item.xfjyjgTywysbm}


    and XZQHDM = #{xzqhdm}
    and JIZ_RQ = #{jizRq}
    and QY_RQ = #{qyRq}
    and cdate = #{cdate}
    and cperson = #{cperson}
    and status = #{status}



        order by ${sort} ${order}


        order by t.cdate desc



    limit #{offset}, #{limit}

select count(*) from jczy_xfsh

    and XFSH_TYWYSBM = #{xfshTywysbm}
    and MC like '%${mc}%'
    and DZMC like '%${dzmc}%'
    and GD = #{gd}
    and DQJD = #{dqjd}
    and DQWD = #{dqwd}
    and SYKYZTLBDM = #{sykyztlbdm}
    and SSLD_MC = #{ssldMc}
    and SSGW_MC = #{ssgwMc}
    and XFJSGWXSLXDM = #{xfjsgwxslxdm}
    and GWZJ_KD = #{gwzjKd}
    and GW_YL = #{gwYl}
    and LL = #{ll}
    and JSGZJ_KD = #{jsgzjKd}
    and CSGZJ_KD = #{csgzjKd}
    and JSCDS_SL = #{jscdsSl}
    and GS_DWMC = #{gsDwmc}
    and GL_DWMC = #{glDwmc}
    and WB_DWMC = #{wbDwmc}
    and LXR_XM = #{lxrXm}
    and LXR_LXDH = #{lxrLxdh}
    and XFJYJG_TYWYSBM = #{xfjyjgTywysbm}
    and XFJYJG_TYWYSBM in

            #{item.xfjyjgTywysbm}


    and XZQHDM = #{xzqhdm}
    and JIZ_RQ = #{jizRq}
    and QY_RQ = #{qyRq}
