mapper.select:
select
zhdj.`id`,
dict1.`name` as zhlx,
dict2.`name` as zhdj,
dict3.`name` as zhcs,
`bkrsLow`,
`bkrsUpp`,
`cdate`,
`status`,
`cperson`
from jczy_zhdj zhdj
LEFT JOIN sys_dict dict1
on dict1.`value` = zhdj.zhlx and dict1.type='JQFLYDM' and dict1.`value` != ''
LEFT JOIN sys_dict dict2
on dict2.`value` = zhdj.zhdj and dict2.type='JQDJDM' and dict2.`value` != ''
LEFT JOIN sys_dict dict3
on dict3.`value` = zhdj.zhcs and dict3.type='ZHCS' and dict3.`value` != ''

  		   and id = #{id} 
  		   and zhlx = #{zhlx} 
  		   and zhdj = #{zhdj} 
  		   and zhcs = #{zhcs} 
  		   and bkrsLow = #{bkrslow} 
  		   and bkrsUpp = #{bkrsupp} 
  		   and cdate = #{cdate} 
  		   and status = #{status} 
  		   and cperson = #{cperson} 

order by zhcs,CAST(zhdj as SIGNED)

	limit #{offset}, #{limit}

select count(*) from jczy_zhdj

  		   and id = #{id} 
  		   and zhlx = #{zhlx} 
  		   and zhdj = #{zhdj} 
  		   and zhcs = #{zhcs} 
  		   and bkrsLow = #{bkrslow} 
  		   and bkrsUpp = #{bkrsupp} 
  		   and cdate = #{cdate} 
  		   and status = #{status} 
  		   and cperson = #{cperson}

SELECT *
FROM `jczy_zhdj`
where `zhlx` = #{zhlx}
AND `zhcs` =#{zhcs}
and (`bkrsLow` <= #{bkrs} and `bkrsUpp` >= #{bkrs})
