mapper.select:
select
t.`menu_id`,t.`parent_id`,t.`name`,t.`url`,t.`perms`,t.`type`,t.`icon`,t.`order_num`,t.`gmt_create`,t.`gmt_modified`,app.`name` as app_name
from sys_menu t
left JOIN
sys_app_info app
ON
t.app_id=app.id

	 and t.menu_id = #{menuId} 
	 and t.parent_id = #{parentId} 
	 and t.`name` = #{name} 
	 and t.url = #{url} 
	 and t.perms = #{perms} 
	 and t.type = #{type} 
	 and t.icon = #{icon} 
	 and t.order_num = #{orderNum} 
	 and t.gmt_create = #{gmtCreate} 
	 and t.gmt_modified = #{gmtModified} 



		order by t.${sort} ${order}


		order by t.menu_id desc



	limit #{offset}, #{limit}

select count(*) from sys_menu

	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 
	 and name = #{name} 
	 and url = #{url} 
	 and perms = #{perms} 
	 and type = #{type} 
	 and icon = #{icon} 
	 and order_num = #{orderNum} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modified = #{gmtModified}
